CREATE TABLE G5_BAIRRO_CIDADE(
   id_bairro            INTEGER  NOT NULL,
   nome_bairro          VARCHAR(19) NOT NULL,
   regional             VARCHAR(43) NOT NULL,
   latitude             NUMERIC(17,11) NOT NULL,
   longitude            NUMERIC(17,11) NOT NULL,
   numero_populacao INTEGER  NOT NULL,
   renda_media          NUMERIC(7,2) NOT NULL,
   PRIMARY KEY (id_bairro)
);

